/**
 * W2 v16: Load Config (YAML)
 * Loads settings, client/batch config, instruction cascade, and builds AI prompt context
 * v15: Added two-agent model configuration
 */
const fs = require('fs');
const yaml = require('js-yaml');

console.log('[W2] â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
console.log('[W2] Starting AI Captions workflow v16 (Two-Agent System)');
console.log('[W2] â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');

const input = $input.first().json || {};
const CONFIG_BASE = '/data/clients/_config';
const SETTINGS_PATH = `${CONFIG_BASE}/settings.json`;
const ACTIVE_JOB_PATH = `${CONFIG_BASE}/active_job.json`;
const DB_PATH = `${CONFIG_BASE}/socialflow.db`;
const AGENTS_PATH = `${CONFIG_BASE}/agents`;
const startTime = Date.now();

// Load settings
if (!fs.existsSync(SETTINGS_PATH)) {
  console.log('[W2] âœ— [CONFIG_MISSING] settings.json not found');
  return [{ json: { _error: true, error_code: 'CONFIG_MISSING', error_message: `settings.json not found at ${SETTINGS_PATH}`, _start_time: startTime, _config_base: CONFIG_BASE } }];
}

let settings;
try {
  settings = JSON.parse(fs.readFileSync(SETTINGS_PATH, 'utf8'));
  console.log('[W2] âœ“ settings.json loaded');
} catch (e) {
  console.log(`[W2] âœ— [CONFIG_MISSING] Failed to parse settings.json: ${e.message}`);
  return [{ json: { _error: true, error_code: 'CONFIG_MISSING', error_message: `Failed to parse settings.json: ${e.message}`, _start_time: startTime, _config_base: CONFIG_BASE } }];
}

// Check database
if (!fs.existsSync(DB_PATH)) {
  console.log(`[W2] âœ— [DB_NOT_FOUND] Database not found at ${DB_PATH}`);
  return [{ json: { _error: true, error_code: 'DB_NOT_FOUND', error_message: `Database not found at ${DB_PATH}. Run init_database.js first.`, _start_time: startTime, _config_base: CONFIG_BASE } }];
}

// Determine client/batch
let CLIENT, BATCH;
if (input.client || input.body?.client) {
  CLIENT = input.client || input.body?.client;
  BATCH = input.batch || input.body?.batch;
  console.log(`[W2] â„¹ Using webhook payload: ${CLIENT}/${BATCH}`);
} else {
  try {
    const activeJob = JSON.parse(fs.readFileSync(ACTIVE_JOB_PATH, 'utf8'));
    CLIENT = activeJob.current?.client || activeJob.client;
    BATCH = activeJob.current?.batch || activeJob.batch;
    console.log(`[W2] â„¹ Using active_job.json: ${CLIENT}/${BATCH}`);
  } catch (e) {
    console.log(`[W2] âœ— [CONFIG_MISSING] Cannot read active_job.json: ${e.message}`);
    return [{ json: { _error: true, error_code: 'CONFIG_MISSING', error_message: 'Cannot determine client/batch. Send {client, batch} in webhook or create active_job.json', _start_time: startTime, _config_base: CONFIG_BASE } }];
  }
}

if (!CLIENT || !BATCH) {
  console.log('[W2] âœ— [CONFIG_MISSING] Both client and batch are required');
  return [{ json: { _error: true, error_code: 'CONFIG_MISSING', error_message: 'Both client and batch are required', _start_time: startTime, _config_base: CONFIG_BASE } }];
}

// Security: Validate slugs contain only safe characters (prevent path traversal)
const SAFE_SLUG = /^[a-zA-Z0-9_-]+$/;
if (!SAFE_SLUG.test(CLIENT) || !SAFE_SLUG.test(BATCH)) {
  console.log('[W2] âœ— [INVALID_SLUG] Invalid characters in client/batch slug');
  return [{ json: { _error: true, error_code: 'INVALID_SLUG', error_message: `Invalid slug format. CLIENT=${CLIENT}, BATCH=${BATCH}. Only alphanumeric, dash, and underscore allowed.`, client: CLIENT, batch: BATCH, _start_time: startTime, _config_base: CONFIG_BASE } }];
}
console.log('[W2] âœ“ Slug validation passed');

const LOCAL_BASE = settings.paths?.docker_base || '/data/clients';
const clientRoot = `${LOCAL_BASE}/${CLIENT}`;
const batchRoot = `${clientRoot}/${BATCH}`;

// Load client.yaml
const clientYamlPath = `${clientRoot}/client.yaml`;
if (!fs.existsSync(clientYamlPath)) {
  console.log(`[W2] âœ— [CLIENT_NOT_FOUND] client.yaml not found at ${clientYamlPath}`);
  return [{ json: { _error: true, error_code: 'CLIENT_NOT_FOUND', error_message: `Client not found: ${CLIENT}. No client.yaml at ${clientYamlPath}`, client: CLIENT, batch: BATCH, _start_time: startTime, _config_base: CONFIG_BASE } }];
}

let clientConfig;
try {
  clientConfig = yaml.load(fs.readFileSync(clientYamlPath, 'utf8'));
  console.log(`[W2] âœ“ client.yaml loaded: ${clientConfig.name || CLIENT}`);
} catch (e) {
  console.log(`[W2] âœ— [YAML_PARSE_ERROR] Failed to parse client.yaml: ${e.message}`);
  return [{ json: { _error: true, error_code: 'YAML_PARSE_ERROR', error_message: `Failed to parse client.yaml: ${e.message}`, client: CLIENT, batch: BATCH, _start_time: startTime, _config_base: CONFIG_BASE } }];
}

if (clientConfig.is_active === false) {
  console.log(`[W2] âœ— [CLIENT_INACTIVE] Client "${CLIENT}" is not active`);
  return [{ json: { _error: true, error_code: 'CLIENT_INACTIVE', error_message: `Client "${CLIENT}" is not active (is_active: false in client.yaml)`, client: CLIENT, batch: BATCH, _start_time: startTime, _config_base: CONFIG_BASE } }];
}

// Load batch.yaml
const batchYamlPath = `${batchRoot}/batch.yaml`;
if (!fs.existsSync(batchYamlPath)) {
  console.log(`[W2] âœ— [BATCH_NOT_FOUND] batch.yaml not found at ${batchYamlPath}`);
  return [{ json: { _error: true, error_code: 'BATCH_NOT_FOUND', error_message: `Batch not found: ${BATCH}. No batch.yaml at ${batchYamlPath}`, client: CLIENT, batch: BATCH, _start_time: startTime, _config_base: CONFIG_BASE } }];
}

let batchConfig;
try {
  batchConfig = yaml.load(fs.readFileSync(batchYamlPath, 'utf8'));
  console.log(`[W2] âœ“ batch.yaml loaded: ${batchConfig.name || BATCH}`);
} catch (e) {
  console.log(`[W2] âœ— [YAML_PARSE_ERROR] Failed to parse batch.yaml: ${e.message}`);
  return [{ json: { _error: true, error_code: 'YAML_PARSE_ERROR', error_message: `Failed to parse batch.yaml: ${e.message}`, client: CLIENT, batch: BATCH, _start_time: startTime, _config_base: CONFIG_BASE } }];
}

// === LOAD HASHTAGS ===
// Get hashtags from batch.yaml and client.yaml
const batchHashtags = batchConfig.hashtags || [];
const clientHashtags = clientConfig.hashtags || [];
const allHashtags = [...new Set([...batchHashtags, ...clientHashtags])];
console.log(`[W2] âœ“ Loaded ${allHashtags.length} hashtags from config`);

// === INSTRUCTION CASCADE (Phase 3) ===
// Load language-specific system prompt
const clientLang = clientConfig.language || 'fr';
const langSuffix = clientLang === 'en' ? '_en' : '_fr';
let systemPrompt = '';
try {
  const langPromptPath = `${AGENTS_PATH}/caption_generator${langSuffix}.md`;
  const defaultPromptPath = `${AGENTS_PATH}/caption_generator.md`;

  if (fs.existsSync(langPromptPath)) {
    systemPrompt = fs.readFileSync(langPromptPath, 'utf8');
    console.log(`[W2] âœ“ Language-specific prompt loaded: ${langSuffix} (${systemPrompt.length} chars)`);
  } else if (fs.existsSync(defaultPromptPath)) {
    systemPrompt = fs.readFileSync(defaultPromptPath, 'utf8');
    console.log(`[W2] âœ“ Default system prompt loaded (${systemPrompt.length} chars)`);
  }
} catch (e) {
  console.log(`[W2] âš  Could not load system prompt: ${e.message}`);
}

// Load client and batch instruction overrides from database
let clientOverride = '';
let batchOverride = '';
let clientId = null;
let batchId = null;
let overrideDb = null;

try {
  const Database = require('better-sqlite3');
  overrideDb = new Database(DB_PATH, { readonly: true });

  // Get client ID
  const clientRow = overrideDb.prepare('SELECT id FROM clients WHERE slug = ?').get(CLIENT);
  if (clientRow) {
    clientId = clientRow.id;
    const clientInstr = overrideDb.prepare(`
      SELECT instruction_value FROM agent_instructions
      WHERE agent_type = 'caption_generator'
      AND scope = 'client' AND scope_id = ?
      AND instruction_key = 'override' AND is_active = 1
    `).get(clientRow.id);
    if (clientInstr) {
      clientOverride = clientInstr.instruction_value;
      console.log(`[W2] âœ“ Client instruction override loaded (${clientOverride.length} chars)`);
    }

    // Get batch ID and override
    const batchRow = overrideDb.prepare('SELECT id FROM batches WHERE client_id = ? AND slug = ?').get(clientRow.id, BATCH);
    if (batchRow) {
      batchId = batchRow.id;
      const batchInstr = overrideDb.prepare(`
        SELECT instruction_value FROM agent_instructions
        WHERE agent_type = 'caption_generator'
        AND scope = 'batch' AND scope_id = ?
        AND instruction_key = 'override' AND is_active = 1
      `).get(batchRow.id);
      if (batchInstr) {
        batchOverride = batchInstr.instruction_value;
        console.log(`[W2] âœ“ Batch instruction override loaded (${batchOverride.length} chars)`);
      }
    }
  }
} catch (e) {
  console.log(`[W2] âš  Could not load instruction overrides: ${e.message}`);
} finally {
  if (overrideDb) overrideDb.close();
}

// Build merged instruction context
const instructionContext = [
  systemPrompt,
  clientOverride ? `\n--- CLIENT-SPECIFIC INSTRUCTIONS ---\n${clientOverride}` : '',
  batchOverride ? `\n--- BATCH-SPECIFIC INSTRUCTIONS ---\n${batchOverride}` : ''
].filter(Boolean).join('\n');

// Build prompt context
const brand = clientConfig.brand || {};
const brief = batchConfig.brief || '';

if (!brief || brief.trim() === '') {
  console.log('[W2] âš  No brief found in batch.yaml - AI captions may be generic');
}

// v15: Two-agent model configuration
const OLLAMA_URL = settings.ollama?.url_docker || 'http://host.docker.internal:11434/api/generate';
const GENERATOR_MODEL = settings.ollama?.models?.caption_generator || settings.caption_generation?.generator_model || 'llama3.2:3b';
const SUPERVISOR_MODEL = settings.ollama?.models?.caption_supervisor || settings.caption_generation?.supervisor_model || 'llama3.2:3b';
const MAX_ROUNDS = settings.caption_generation?.max_revision_rounds || 3;
const GENERATOR_TIMEOUT = settings.caption_generation?.generator_timeout_ms || settings.ollama?.timeout_ms || 120000;
const SUPERVISOR_TIMEOUT = settings.caption_generation?.supervisor_timeout_ms || 60000;

console.log(`[W2] â„¹ Generator model: ${GENERATOR_MODEL}`);
console.log(`[W2] â„¹ Supervisor model: ${SUPERVISOR_MODEL}`);
console.log(`[W2] â„¹ Max revision rounds: ${MAX_ROUNDS}`);

return [{ json: {
  client: CLIENT,
  batch: BATCH,
  client_id: clientId,
  batch_id: batchId,
  batch_root: batchRoot,
  source: input.body ? 'webhook' : 'active_job',
  ollama_url: OLLAMA_URL,
  generator_model: GENERATOR_MODEL,
  supervisor_model: SUPERVISOR_MODEL,
  max_rounds: MAX_ROUNDS,
  generator_timeout: GENERATOR_TIMEOUT,
  supervisor_timeout: SUPERVISOR_TIMEOUT,
  instruction_context: instructionContext,
  config_hashtags: allHashtags,
  client_name: clientConfig.name || CLIENT,
  client_language: clientLang,
  brand_voice: brand.voice || 'Professionnelle et engageante',
  brand_target_audience: brand.target_audience || 'Grand public',
  brand_description: brand.description || '',
  batch_name: batchConfig.name || BATCH,
  batch_brief: brief,
  db_path: DB_PATH,
  _start_time: startTime,
  _config_base: CONFIG_BASE
} }];

/**
 * W2 v16: Get NEEDS_AI Items
 * Queries database for items requiring AI caption generation
 * v15: Also fetches image_description from W1
 */
const Database = require('better-sqlite3');
const config = $input.first().json;

console.log(`[W2] â„¹ Opening database: ${config.db_path}`);

let db;
try {
  db = new Database(config.db_path, { readonly: true });
} catch (e) {
  console.log(`[W2] âœ— [DB_ERROR] Failed to open database: ${e.message}`);
  return [{ json: { _error: true, error_code: 'DB_ERROR', error_message: `Failed to open database: ${e.message}`, client: config.client, batch: config.batch, _start_time: config._start_time, _config_base: config._config_base } }];
}

try {
  // v15: Added image_description to query
  const query = `
    SELECT ci.*, c.slug as client_slug, b.slug as batch_slug, b.brief
    FROM content_items ci
    JOIN clients c ON ci.client_id = c.id
    JOIN batches b ON ci.batch_id = b.id
    WHERE ci.status = 'NEEDS_AI' AND c.slug = ? AND b.slug = ?
    ORDER BY ci.scheduled_date, ci.scheduled_time
  `;

  const items = db.prepare(query).all(config.client, config.batch);
  console.log(`[W2] â„¹ Found ${items.length} NEEDS_AI items`);

  if (items.length === 0) {
    console.log('[W2] âš  [NO_ITEMS] No NEEDS_AI items found');
    return [{ json: { _skip: true, _config: config, _no_items: true, message: `No NEEDS_AI items for ${config.client}/${config.batch}`, count: 0 } }];
  }

  // Count items with/without descriptions
  const withDesc = items.filter(i => i.image_description && i.image_description.trim()).length;
  console.log(`[W2] â„¹ Items with image description: ${withDesc}/${items.length}`);

  return items.map((item, index) => ({
    json: {
      ...item,
      _config: config,
      _item_index: index + 1,
      _total_items: items.length
    }
  }));

} catch (e) {
  console.log(`[W2] âœ— [DB_ERROR] Database query failed: ${e.message}`);
  return [{ json: { _error: true, error_code: 'DB_ERROR', error_message: `Database query failed: ${e.message}`, client: config.client, batch: config.batch, _start_time: config._start_time, _config_base: config._config_base } }];
} finally {
  if (db) db.close();
}

/**
 * W2 v16: Initialize Generation
 * Sets up generation progress tracking and initializes batch state
 */
const Database = require('better-sqlite3');
const items = $input.all();

if (!items || items.length === 0) return items;

const config = items[0].json._config;
const totalItems = items.length;

console.log(`[W2] â„¹ Initializing generation for ${totalItems} items`);

// Update batch generation_progress
let db;
try {
  db = new Database(config.db_path);
  db.pragma('foreign_keys = ON');

  const progress = JSON.stringify({
    current: 0,
    total: totalItems,
    stage: 'starting',
    round: 0,
    started_at: new Date().toISOString()
  });

  db.prepare(`
    UPDATE batches SET
      generation_progress = ?,
      generation_started_at = datetime('now')
    WHERE client_id = ? AND slug = ?
  `).run(progress, config.client_id, config.batch);

  console.log('[W2] âœ“ Generation progress initialized');
} catch (e) {
  console.log(`[W2] âš  Could not initialize progress: ${e.message}`);
} finally {
  if (db) db.close();
}

return items;

/**
 * W2 v16: Update Progress (Start)
 * Updates generation progress for current item
 * OPTIMIZATION: Debounced updates - only writes to DB every 2 seconds or at milestones
 */
const Database = require('better-sqlite3');
const item = $json;
const config = item._config;

console.log(`[W2] â„¹ Starting item ${item._item_index}/${item._total_items}: ${item.content_id}`);

// Debouncing: Only update progress at milestones or every 2 seconds
const isFirstItem = item._item_index === 1;
const isLastItem = item._item_index === item._total_items;
const isMilestone = item._item_index % 5 === 0;  // Every 5th item
const lastUpdate = config._last_progress_update || 0;
const now = Date.now();
const shouldUpdate = isFirstItem || isLastItem || isMilestone || (now - lastUpdate > 2000);

if (shouldUpdate) {
  let db;
  try {
    db = new Database(config.db_path);
    const progress = JSON.stringify({
      current: item._item_index,
      total: item._total_items,
      stage: 'generating',
      round: 1,
      content_id: item.content_id
    });

    db.prepare(`
      UPDATE batches SET generation_progress = ?
      WHERE client_id = ? AND slug = ?
    `).run(progress, config.client_id, config.batch);
    config._last_progress_update = now;
  } catch (e) {
    console.log(`[W2] âš  Progress update failed: ${e.message}`);
  } finally {
    if (db) db.close();
  }
}

// Generate unique session ID for conversation logging
const crypto = require('crypto');
const sessionId = `${item.content_id}_${Date.now()}_${crypto.randomBytes(4).toString('hex')}`;

return {
  json: {
    ...item,
    _session_id: sessionId,
    _round_number: 1,
    _conversation_log: [],
    _revision_history: [],
    _config: { ...config, _last_progress_update: config._last_progress_update || now }
  }
};

/**
 * W2 v16: Prepare Generator Request
 * Builds text-only prompt using image_description from W1
 * v15: Uses text LLM, not VLM - image_description provides visual context
 * BUG FIX #3: For videos, use batch brief as context if no image_description
 * BUG FIX #4: Show ALL revision feedback, not just the last one
 */
const fs = require('fs');
const item = $json;
const config = item._config;

console.log(`[W2] â„¹ Preparing generator request (round ${item._round_number}): ${item.content_id}`);

// BUG FIX #3: For videos without image_description, use batch brief as context
let imageContext = item.image_description || '';
if (!imageContext && item.media_type === 'video') {
  imageContext = `[Video content] ${config.batch_brief || 'Create an engaging caption for this video content.'}`;
  console.log('[W2] â„¹ Video without image description - using batch brief as context');
}

const lang = config.client_language || 'fr';
const isVideo = item.media_type === 'video';
const isTikTok = (item.platforms || '').includes('tt');

// BUG FIX #4: Build revision context showing ALL previous feedback
let revisionContext = '';
if (item._revision_history && item._revision_history.length > 0) {
  const allFeedback = item._revision_history.map((h, i) => 
    `Round ${i + 1}: "${h.caption}" - Feedback: ${h.feedback}`
  ).join('\n');
  revisionContext = `

=== REVISION HISTORY ===
${allFeedback}

CrÃ©er une nouvelle lÃ©gende en tenant compte de TOUS ces retours.
=== FIN RÃ‰VISION ===
`;
}

// Build language-specific revision context
let revisionContextEn = '';
if (item._revision_history && item._revision_history.length > 0) {
  const allFeedback = item._revision_history.map((h, i) => 
    `Round ${i + 1}: "${h.caption}" - Feedback: ${h.feedback}`
  ).join('\n');
  revisionContextEn = `

=== REVISION HISTORY ===
${allFeedback}

Create a new caption taking into account ALL of this feedback.
=== END REVISION ===
`;
}

// French prompt for text LLM
const promptFr = `Tu es rÃ©dacteur social media pour ${config.client_name}.

CONTEXTE DE LA MARQUE:
- Ton: ${config.brand_voice}
- Audience cible: ${config.brand_target_audience}
${config.brand_description ? `- Description: ${config.brand_description}` : ''}

BRIEF DU PROJET "${config.batch_name}":
${config.batch_brief || 'Aucun brief spÃ©cifique.'}

${imageContext ? `DESCRIPTION DE L'IMAGE (pour inspiration, NE PAS dÃ©crire littÃ©ralement):
${imageContext}` : 'Pas de description d\'image disponible - base-toi uniquement sur le brief.'}

CONSIGNES:
- Ã‰cris une lÃ©gende ${isTikTok ? 'TikTok' : 'Instagram'} en 2-3 lignes maximum
- Commence par une accroche captivante (10 mots max)
- Ã‰voque l'expÃ©rience, l'Ã©motion, l'ambiance - PAS une description littÃ©rale
- Termine par un appel Ã  l'action subtil
- ${isTikTok ? 'TikTok: court et punchy' : 'Instagram: engageant et authentique'}

HASHTAGS:
- Termine TOUJOURS par 5-7 hashtags pertinents en franÃ§ais
- Inclus ces hashtags de marque: ${(config.config_hashtags || []).slice(0, 3).join(' ') || '#instagram'}

INTERDIT
- PAS de guillemets autour du texte
- PAS de mention de pays (France, Allemagne, Berlin, Paris, etc.)
- PAS de mots anglais - Ã©cris UNIQUEMENT en franÃ§ais
- PAS de "Voici" ou "LÃ©gende:" au dÃ©but
- PAS de noms de marques/commerces autres que ${config.client_name}${revisionContext}

Ã‰cris UNIQUEMENT la lÃ©gende (sans guillemets):`;

// English prompt for text LLM
const promptEn = `You are a social media copywriter for ${config.client_name}.

BRAND CONTEXT:
- Tone: ${config.brand_voice}
- Target audience: ${config.brand_target_audience}
${config.brand_description ? `- Description: ${config.brand_description}` : ''}

PROJECT BRIEF "${config.batch_name}":
${config.batch_brief || 'No specific brief.'}

${imageContext ? `IMAGE DESCRIPTION (for inspiration, do NOT describe literally):
${imageContext}` : 'No image description available - base your caption only on the brief.'}

INSTRUCTIONS:
- Write a ${isTikTok ? 'TikTok' : 'Instagram'} caption in 2-3 lines maximum
- Start with a catchy hook (10 words max)
- Evoke the experience, emotion, atmosphere - NOT a literal description
- End with a subtle call to action
- ${isTikTok ? 'TikTok: short and punchy' : 'Instagram: engaging and authentic'}

HASHTAGS:
- ALWAYS end with 5-7 relevant hashtags
- Include these brand hashtags: ${(config.config_hashtags || []).slice(0, 3).join(' ') || '#instagram'}

FORBIDDEN
- NO quotation marks around the text
- NO country mentions (France, Germany, Berlin, Paris, etc.)
- NO "Here's" or "Caption:" at the beginning
- NO brand names other than ${config.client_name}${revisionContextEn}

Write ONLY the caption (without quotes):`;

const prompt = lang === 'en' ? promptEn : promptFr;

console.log(`[W2] âœ“ Generator prompt built (${prompt.length} chars, lang: ${lang})`);

return {
  json: {
    ...item,
    _generator_prompt: prompt,
    _generator_start_time: Date.now()
  }
};

/**
 * W2 v16: Process Generator Response
 * Extracts and cleans caption from generator output
 */
const response = $json;
const prev = $('Prepare Generator Request').first()?.json;

if (!prev || !prev.content_id) {
  console.log('[W2] âœ— [MISSING_DATA] No data from Prepare Generator Request node');
  return { json: { _error: true, error_code: 'MISSING_DATA', error_message: 'Missing data from Prepare Generator Request node', status: 'FAILED' } };
}

const generatorDuration = Date.now() - (prev._generator_start_time || Date.now());

// Log the generator conversation
const conversationEntry = {
  agent_type: 'caption_generator',
  agent_model: prev._config.generator_model,
  round_number: prev._round_number,
  prompt: prev._generator_prompt,
  response: response.response || response.error || 'No response',
  duration_ms: generatorDuration,
  status: response.error ? 'error' : 'success',
  error_message: response.error || null
};

const conversationLog = [...(prev._conversation_log || []), conversationEntry];

// Check for errors
if (response.error || !response.response) {
  const errorMsg = response.error || 'No AI response received';
  console.log(`[W2] âœ— [GENERATOR_ERROR] ${prev.content_id}: ${errorMsg}`);

  return { json: {
    ...prev,
    _conversation_log: conversationLog,
    _generator_error: true,
    _error_message: errorMsg,
    status: 'FAILED',
    error_code: 'GENERATOR_ERROR',
    error_message: errorMsg
  } };
}

// Clean the caption
let caption = (response.response || '').trim();

// Remove common LLM artifacts
